<?phpfunction getEditButton($component, $title, $controller){    $user =& JFactory::getUser();    if ($user->authorize($component, 'edit'))    {        return '<span class="hasTip" title="'.$title.'">                    <a href="index.php?option='.$component.'&controller='.$controller.'&task=edit">                        <img src="images/M_images/edit.png" alt="edit" />                    </a>                </span>';    }    return '';}function date_mysql2german($date){    $d    =    explode('-',$date);    return    sprintf('%02d.%02d.%04d', $d[2], $d[1], $d[0]);}function humanReadable($tageszeit){    switch ($tageszeit)    {        case 'V':            return 'Vormittag';        case 'N':            return 'Nachmittag';        default:            return 'Vor- und Nachmittag';    }}