<?php/** * Belegung Model für das Heimbelegung Component *  * @author     R. Baumgartner * @package    Falkenstein.Joomla * @subpackage Components * @link       http://www.pfadi-falkenstein.ch * @license    GNU/GPL */// Sicherheitscheckdefined('_JEXEC') or die('Restricted access');// Import der Basisklassejimport( 'joomla.application.component.model' );/** * Belegung Model * * @package    Falkenstein.Joomla * @subpackage Components */class BelegungModelBelegung extends JModel{    function __construct()    {        parent::__construct();    }    /**     * Liefert alle Belegungen für ein Heim und einen Monat     * @return Alle Belegungen     */    function getBelegung($heim, $start, $stop)    {        $app =& JFactory::getApplication();        $db =& JFactory::getDBO();                $heim = $db->quote($heim);        $start = $db->quote($start);        $stop = $db->quote($stop);                $query = 'SELECT * FROM #__belegung                   WHERE heim = '.$heim.'                  AND ((von BETWEEN '.$start.' AND '.$stop.') OR (bis BETWEEN '.$start.' AND '.$stop.'))';        $db->setQuery( $query );        $belegung = $db->loadObjectList();        if (is_null($belegung))        {            $app->enqueueMessage(nl2br($db->getErrorMsg()),'error');        }        return $belegung;    }}