<?php// Sicherheitscheckdefined('_JEXEC') or die('Restricted access');JHTML::_('behavior.calendar'); // Kalender-Script vorbereiten$document =& JFactory::getDocument();$document->addScriptDeclaration(    "window.addEvent('domready', function() {Calendar.setup({        inputField     :    'von',     // id of the input field        ifFormat       :    '%d.%m.%Y',      // format of the input field        button         :    'von_img',  // trigger for the calendar (button ID)        align          :    'Tl',           // alignment (defaults to 'Bl')        singleClick    :    true    });});");$document->addScriptDeclaration(    "window.addEvent('domready', function() {Calendar.setup({        inputField     :    'bis',     // id of the input field        ifFormat       :    '%d.%m.%Y',      // format of the input field        button         :    'bis_img',  // trigger for the calendar (button ID)        align          :    'Tl',           // alignment (defaults to 'Bl')        singleClick    :    true    });});");function date_mysql2german($date){    $d    =    explode("-",$date);    return    sprintf("%02d.%02d.%04d", $d[2], $d[1], $d[0]);}?><script language="javascript" type="text/javascript"><!--function submitbutton(pressbutton){    submitform(pressbutton);}//--></script><style type="text/css">.input {    width: 100%;}textarea.input {    height: 100px;}</style><form action="index.php" method="post" name="adminForm" id="adminForm">    <fieldset>    <legend>Belegung</legend>    <table class='admintable'>        <tr>            <td class='key'>                <label for='von'>Von:</label>            </td>            <td>                <input style='vertical-align:top;width:80%' class='input' type='text' name='von' id='von' value='<?php echo date_mysql2german($this->belegung->von); ?>' />                <img class="calendar" src="templates/system/images/calendar.png" alt="calendar" name='von_img' id="von_img"